# Migrate act.patientClinicalProblem for OVPMS-1468
#
DROP TABLE IF EXISTS migrate_problems;
CREATE TABLE migrate_problems (
  act_id  BIGINT PRIMARY KEY,
  problem VARCHAR(255),
  code    VARCHAR(255)
);

  SELECT
    a.act_id,
    d.value,
    upper(d.value)
  FROM acts a JOIN act_details d
      ON a.act_id = d.act_id AND d.name = "problem"
  WHERE a.arch_short_name = "act.patientClinicalProblem" AND d.value IS NOT null AND d.value <> "";

UPDATE migrate_problems
SET code = replace(code, " ", "_");

UPDATE migrate_problems
SET code = replace(code, ".", "_");

UPDATE migrate_problems
SET code = replace(code, "&", "_");

UPDATE migrate_problems
SET code = replace(code, "/", "_");

UPDATE migrate_problems
SET code = replace(code, "\\", "_");

UPDATE migrate_problems
SET code = replace(code, "(", "_");

UPDATE migrate_problems
SET code = replace(code, ")", "_");

UPDATE migrate_problems
SET code = replace(code, "*", "_");

UPDATE migrate_problems
SET code = replace(code, ",", "_");

UPDATE migrate_problems
SET code = replace(code, "-", "_");

UPDATE migrate_problems
SET code = replace(code, "[", "_");

UPDATE migrate_problems
SET code = replace(code, "]", "_");

UPDATE migrate_problems
SET code = replace(code, "__", "_");

  SELECT
    0,
    UUID(),
    'lookup.diagnosis',
    1,
    '1.0',
    code,
    problem,
    null,
    0
  FROM (SELECT
          code,
          problem
        FROM migrate_problems
        GROUP BY code) a
  WHERE NOT exists(SELECT
                     *
                   FROM lookups e
                   WHERE e.arch_short_name = 'lookup.diagnosis' AND e.code = a.code);

UPDATE acts a,
  migrate_problems p
SET a.reason = p.code
WHERE a.act_id = p.act_id;

DELETE d
FROM acts a,
  act_details d,
  migrate_problems p
WHERE a.act_id = d.act_id AND a.act_id = p.act_id
      AND d.name = "problem";

DROP TABLE migrate_problems;

