#
# Add VeNom diagnosis codes for OVPMS-1468
#
DROP TABLE IF EXISTS venom_diagnosis;
CREATE TABLE venom_diagnosis (
  code         VARCHAR(255) PRIMARY KEY,
  dictionaryId VARCHAR(255),
  term         VARCHAR(255),
  modelling    VARCHAR(255));
  SELECT
    0,
    UUID(),
    'lookup.diagnosisVeNom',
    1,
    '1.0',
    d.code,
    d.term,
    d.modelling,
    0
  FROM venom_diagnosis d
  WHERE NOT exists(SELECT
                     *
                   FROM lookups e
                   WHERE e.arch_short_name = 'lookup.diagnosisVeNom' AND e.code = d.code);
  SELECT
    l.lookup_id,
    'dataDictionaryId',
    'string',
    d.dictionaryId
  FROM lookups l, venom_diagnosis d
  WHERE l.arch_short_name = 'lookup.diagnosisVeNom' AND l.code = d.code
        AND NOT exists(SELECT
                         *
                       FROM lookup_details d
                       WHERE d.lookup_id = l.lookup_id AND d.name = 'dataDictionaryId');
DROP TABLE venom_diagnosis;

